package mypackage;

public class Q_1_Control_flow_if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 10;
		
		if(number>0) {
			System.out.println("Positive number");
		}
		else if(number==0)
		{
			System.out.println("Zero");
		}
		else {
			System.out.println("Negative number");
		}
		

	}

}
